// ... existing code ...
/* Dark mode theme variables and overrides */
:root {
  --morph-bg: #0f1115;
  --morph-surface: #161a22;
  --morph-text: #e6e9ef;
  --morph-muted: #9aa4b2;
  --morph-primary: #7aa2f7;
  --morph-accent: #9ece6a;
  --morph-border: rgba(255, 255, 255, 0.08);
}

html, body {
  background: var(--morph-bg) !important;
  color: var(--morph-text) !important;
}

/* Containers */
header, nav, section, article, aside, footer,
.card, .panel, .container, .content, .box {
  background: var(--morph-surface) !important;
  color: var(--morph-text) !important;
  border-color: var(--morph-border) !important;
}

/* Text */
h1, h2, h3, h4, h5, h6, p, span, li, dt, dd, label, small {
  color: var(--morph-text) !important;
}

/* Muted text */
sub, sup, .muted, .text-muted, .hint, .help, .description {
  color: var(--morph-muted) !important;
}

/* Links */
a, a:visited {
  color: var(--morph-primary) !important;
}
a:hover {
  color: #a5b8ff !important;
}

/* Inputs */
input, select, textarea {
  background: #0f131a !important;
  color: var(--morph-text) !important;
  border: 1px solid var(--morph-border) !important;
}

/* Buttons */
button, .btn {
  background: #1b2230 !important;
  color: var(--morph-text) !important;
  border: 1px solid var(--morph-border) !important;
  border-radius: 8px !important;
}
button:hover, .btn:hover {
  filter: brightness(1.1) !important;
}

/* Tables */
table {
  border-color: var(--morph-border) !important;
}
th, td {
  border-color: var(--morph-border) !important;
  color: var(--morph-text) !important;
}

/* Code blocks */
pre, code {
  background: #10141b !important;
  color: #e3e8f5 !important;
  border: 1px solid var(--morph-border) !important;
}

/* Elevation */
.card, .panel, .box, .popover, .tooltip {
  box-shadow: 0 8px 24px rgba(0,0,0,0.35) !important;
}

// ... existing code ...
/* Rounded modern UI polish */
:root {
  --morph-radius: 12px;
  --morph-shadow: 0 10px 24px rgba(0,0,0,0.12);
  --morph-transition: 180ms ease;
}

button, .btn, input, select, textarea, .card, .panel, .box, .chip, .tag, .badge, .modal, .dropdown, .tooltip {
  border-radius: var(--morph-radius) !important;
  transition: all var(--morph-transition) !important;
}

.card, .panel, .box, .popover, .dropdown-menu {
  box-shadow: var(--morph-shadow) !important;
  border: 1px solid rgba(0,0,0,0.06) !important;
}

button:hover, .btn:hover {
  transform: translateY(-1px);
  box-shadow: 0 12px 30px rgba(0,0,0,0.14) !important;
}

input:focus, select:focus, textarea:focus, button:focus, .btn:focus {
  outline: none !important;
  box-shadow: 0 0 0 3px rgba(102,126,234,0.18) !important;
}

// ... existing code ...
/* High contrast and a11y improvements */
:root {
  --hc-text: #111;
  --hc-bg: #fff;
  --hc-border: #111;
  --hc-focus: #0a84ff;
}

body {
  color: var(--hc-text) !important;
  background: var(--hc-bg) !important;
}

a, a:visited {
  color: #0a58ca !important;
  text-decoration-thickness: 0.12em !important;
}

button, .btn, input, select, textarea {
  min-height: 40px !important;
  border: 2px solid var(--hc-border) !important;
}

*:focus {
  outline: 3px solid var(--hc-focus) !important;
  outline-offset: 2px !important;
}

.text-muted, .muted {
  color: #333 !important;
}
